/* 2 ITEM */
/* O vendedor que mais aplicou descontos em produtos de higiene */
SELECT vendedor, categoria_produto, MAX(TOTAL) FROM (SELECT vendedor, categoria_produto, SUM(desconto) AS TOTAL FROM exercicio2 WHERE categoria_produto = 'HIGIENE' GROUP BY vendedor) AS tempTable;

/* total de desconto aplicado sobre os produtos de higiene*/ 
SELECT vendedor, categoria_produto, SUM(desconto) AS total_desconto FROM exercicio2 WHERE categoria_produto = 'HIGIENE'; 

/* total de desconto aplicado sobre todos os produtos*/ 
SELECT SUM(desconto) FROM exercicio2;

/* desconto médio sobre os produtos de higiene */
SELECT vendedor, categoria_produto, AVG(desconto) AS desconto_medio FROM exercicio2 WHERE categoria_produto = 'HIGIENE'; 

/* desconto médio sobre todos os produtos */
SELECT AVG(desconto) AS desconto_medio FROM exercicio2; 
