/* 3 ITEM */
/* o cliente que teve o custo médio mas alto em produtos de casa e a quantidade total de itens adquiridos por ele*/
DELIMITER $$
CREATE PROCEDURE custo_medio_casa()
BEGIN

CREATE VIEW `custos` AS SELECT nome_cliente, quantidade, preco_unitario, quantidade*preco_unitario AS custo_total FROM exercicio2 WHERE categoria_produto = 'CASA' ORDER BY nome_cliente;
SELECT nome_cliente, SUM(quantidade), SUM(preco_unitario), SUM(custo_total) AS custo_total FROM custos GROUP BY nome_cliente;
CREATE VIEW `maior_custo_medio` AS SELECT nome_cliente, SUM(quantidade), SUM(custo_total)/SUM(quantidade) AS custo_medio FROM custos GROUP BY nome_cliente;
SELECT * FROM maior_custo_medio ORDER BY custo_medio DESC LIMIT 1;
DROP VIEW IF EXISTS custos;
DROP VIEW IF EXISTS maior_custo_medio;

END $$
DELIMITER ;

CALL custo_medio_casa();
